import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Dashboard.css'; // Create this CSS file for styling

function Dashboard() {
  const [loginHistory, setLoginHistory] = useState([]);
  const [activities, setActivities] = useState([]); // To track exercises
  const [nutrition, setNutrition] = useState([]);   // To track nutrition info
  const [goals, setGoals] = useState([]);           // To track fitness goals

  useEffect(() => {
    // Fetch login history on component mount
    const fetchLoginHistory = async () => {
      try {
        const response = await axios.get('http://localhost:5000/login-history');
        setLoginHistory(response.data);
      } catch (error) {
        console.error('Error fetching login history', error);
      }
    };

    fetchLoginHistory();
  }, []);

  // Placeholder data for activities, nutrition, and goals
  useEffect(() => {
    // This should ideally come from a backend API
    setActivities([
      { id: 1, name: 'Running', duration: '30 mins', caloriesBurned: 300 },
      { id: 2, name: 'Yoga', duration: '45 mins', caloriesBurned: 200 },
    ]);
    setNutrition([
      { id: 1, meal: 'Breakfast', calories: 400 },
      { id: 2, meal: 'Lunch', calories: 600 },
    ]);
    setGoals([
      { id: 1, goal: 'Run 5 km', status: 'In Progress' },
      { id: 2, goal: 'Drink 2 liters of water daily', status: 'Achieved' },
    ]);
  }, []);

  return (
    <div className="dashboard-container">
      <h2>Health and Fitness Dashboard</h2>
      <h3>Welcome! Hereâ€™s your activity summary:</h3>

      <section>
        <h4>Login History</h4>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Login Time</th>
            </tr>
          </thead>
          <tbody>
            {loginHistory.length > 0 ? (
              loginHistory.map((event, index) => (
                <tr key={index}>
                  <td>{event.username}</td>
                  <td>{new Date(event.timestamp).toLocaleString()}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="2" style={{ textAlign: 'center' }}>No login history available</td>
              </tr>
            )}
          </tbody>
        </table>
      </section>

      <section>
        <h4>Your Activities</h4>
        <ul>
          {activities.map(activity => (
            <li key={activity.id}>
              {activity.name}: {activity.duration} - {activity.caloriesBurned} calories burned
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h4>Your Nutrition</h4>
        <ul>
          {nutrition.map(meal => (
            <li key={meal.id}>
              {meal.meal}: {meal.calories} calories
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h4>Your Goals</h4>
        <ul>
          {goals.map(goal => (
            <li key={goal.id}>
              {goal.goal}: {goal.status}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default Dashboard;
